mapper.update:
UPDATE board 
SET 
	board_title = #{boardTitle}, 
	board_content = #{boardContent} 
WHERE board_no = #{boardNo} AND board_pw = #{boardPw}

mapper.select:
SELECT board_no as boardNo, board_title as boardTitle, board_content as boardContent, board_user as boardUser, board_date as boardDate FROM board
board_title LIKE CONCAT("%",#{searchWord},"%")
ORDER BY board_date desc LIMIT #{beginRow},#{rowPerPage}
